import { CartProvider, useCart } from "react-use-cart";
import Navbar from "../Homepage/Navbar";
import { Col, Container, Row } from "reactstrap";

function Page() {
    const { addItem } = useCart();

   


    return (
        <div>
            <Container>
                <Row>
                    {products.map((item) => (
                        <Col md="4" key={item.id} className="text-center">
                            <img src={item.images[0]} className="img-fluid" style={{ height: "350px" }}></img>
                            <h6>{item.title}</h6>
                            <button className="mb-4 btn btn-primary" onClick={() => addItem(item)}>Add to cart</button>
                        </Col>
                    ))}

                </Row>

            </Container>
        </div>
    );
}



function AllShopping() {
    return (
        <CartProvider>
            <Navbar></Navbar>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <br></br>
            <Page />
            
        </CartProvider>
    );
}
export default AllShopping;
